<script>
    /**
     * Bootstrap Dropdown for Vue Component
     */
    
     import default {
        props: {
            text: String,
            icon: {
                type: String,
                default: 'fa fa-angle-down'
            },
            btnClass: {
                type: String,
                default: 'btn btn-outline blue'
            },
            direction: {
                type: String,
                default: 'left'
            }
        },
        computed: {
            menuClass () {
                return this.direction === 'right' ? 'pull-right' : '';
            }
        }
     };
</script>

<template>
    <div class="btn-group">
        <a href="javascript:;" data-toggle="dropdown" :class="btnClass"> 
            {{text}}
            <i :class="icon"></i>
        </a>
        <ul class="dropdown-menu" :class="menuClass">
            <slot>
            </slot>
        </ul>
    </div>
</template>